    constructor(props) {
        super(props);

        this.state = {

            username: props.username,
            name: "",
            avatar_url: "",
            company: "",
            location: "",
            public_repos: "0",
            followers: "0",
            gists: "0",
            error: null,
            isLoading: true

        }
    }

    componentDidMount(){
        console.log("SSS");


        const url="https://api.github.com/users/" + this.state.username;
        fetch(url).then(result=>{
            if(result.ok) {
                return result.json();

